$(document).ready(function(){$(".pull-left a, .jumbotron a[href='#learn-more'], .about a[href='#footer'], .footer a[href='#jumbo']").on("click",function(o){o.preventDefault();var t=this.hash;$("html, body").animate({scrollTop:$(t).offset().top},900,function(){window.location.hash=t})})}),$(window).scroll(function(){$(".hiddenStuff").each(function(){$(this).offset().top<$(window).scrollTop()+600&&$(this).addClass("visibleStuff")})});